mapper.insert:
INSERT INTO p_group_member (
	group_no, user_no
	, role
	, nickname
)
VALUES (
	#{group_no}, #{user_no}
	, #{role}
	, #{nickname}
)

mapper.update:
UPDATE p_group 
SET group_name = #{group_name}, 
	description = #{description},
	capacity = #{capacity},
	is_public = #{is_public}

		,profile_img = #{profile_img}

WHERE group_no = #{group_no}
