mapper.select:
SELECT gm.message_no, gm.group_no, gm.sender_no, gm.content, gm.message_type, gm.sent_at, gm.sender_nickname, u.profile FROM p_group_msg gm JOIN p_users u ON gm.sender_no = u.user_no WHERE gm.group_no = #{group_no}
AND gm.message_no < #{last_message_no}
ORDER BY gm.message_no DESC FETCH FIRST 35 ROWS ONLY

SELECT * FROM p_group_msg 
WHERE group_no = #{groupNo} 

	AND content LIKE CONCAT('%', #{keyword}, '%') 


	AND sender_nickname LIKE CONCAT('%', #{senderNickname}, '%') 


	AND sent_at BETWEEN #{startDate} AND #{endDate}

ORDER BY sent_at ASC

(
	SELECT * FROM (
		SELECT * FROM p_group_msg 
		WHERE group_no = #{groupNo} AND message_no < #{messageNo} 
		ORDER BY message_no DESC 
		FETCH FIRST 10 ROWS ONLY 
	)
	ORDER BY message_no ASC
)
UNION ALL
(
	SELECT * FROM p_group_msg 
	WHERE group_no = #{groupNo} AND message_no >= #{messageNo} 
	ORDER BY message_no ASC 
	FETCH FIRST 11 ROWS ONLY
)
